import React, { Fragment, useState } from 'react'
import { TextField, Layout } from '@shopify/polaris';

const Question = () => {

    const [options, setOptions] = useState(0);
    if (options) {
        console.log(options);
    }
    return (
        <Fragment>
            <TextField
                value={() => { }}
                onChange={() => { }}
                label="Question"
                type="text"
                placeholder="Which of the following do you prefer, etc"
            />
            <TextField
                value={() => { }}
                onChange={num => setOptions(num)}
                label="Number of options"
                type="number"
                placeholder="Which of the following do you prefer, etc"
            />
        </Fragment>
    )
}

export default Question;
