import React, { Fragment, useState, useCallback, useEffect } from 'react';
import { TextField, Layout, Form, FormLayout, Button } from '@shopify/polaris';
import _ from 'lodash';

const Question = () => {
  const [options, setOptions] = useState({
    'Option 1': '',
    'Option 2': '',
    'Option 3': '',
    'Option 4': ''
  });
  const [question, setQuestion] = useState('');
  const [numOptions, setNumOptions] = useState(2);

  const handleNumOptions = useCallback(option => {
    if (option > 4) {
      setNumOptions(4), [];
    } else if (option < 2) {
      setNumOptions(2), [];
    } else {
      setNumOptions(option), [];
    }
  });
  const handleQuestion = useCallback(question => setQuestion(question), []);
  const handleSubmit = useCallback(_event => setUrl(''), []);

  const displayOptions = opts => {
    if (!opts) {
      return <div>Loading...</div>;
    }

    return opts.map(item => {
      const optionName = `Option ${item}`;
      return (
        <TextField
          label={optionName}
          name={optionName}
          type='text'
          value={options[optionName]}
          onChange={e => console.log(e)}
          key={item}
        />
      );
    });
  };

  return (
    <Fragment>
      <Form noValidate onSubmit={handleSubmit}>
        <FormLayout>
          <TextField
            value={question}
            onChange={handleQuestion}
            label='Question'
            type='text'
            placeholder='Which of the following do you prefer, etc'
          />
          <TextField
            label='Number of options'
            type='number'
            maxLength='1'
            max='4'
            min='2'
            value={numOptions}
            onChange={handleNumOptions}
          />
          <Button primary>Add Option</Button>
          <Button secondary>Remove Option</Button>
          {displayOptions(_.range(1, parseInt(numOptions, 10) + 1))}
        </FormLayout>
      </Form>
    </Fragment>
  );
};

export default Question;
