import React, { Fragment, useState } from 'react'
import { TextField, Layout } from '@shopify/polaris';

const Question = () => {

    const [options, setOptions] = useState(0);
    const [question, setQuestion] = useState('');

    const handleOptions = useCallback((option) => setOptions(option), []);

    return (
        <Fragment>
            <TextField
                value={question}
                onChange={q => setQuestion(q)}
                label="Question"
                type="text"
                placeholder="Which of the following do you prefer, etc"
            />
            <TextField
                label="Number of options"
                type="number"
                value={options}
                onChange={handleOptions}

            />
        </Fragment>
    )
}

export default Question;
